import React, {Component} from 'react';

class BplStandings extends Component{

    state = 
  {
    data: [],
  }

  componentDidMount(){
    fetch('http://localhost:8080/competitions/2021/standings')
    .then(response => response.json())
    .then(data => {
      console.log(data);
      this.setState({data})
    }
    );
    }

    render(){
    return(
            <div className="standing">STANDINGS
            {this.state.data.map((standing) =>{
                return standing.type == "TOTAL"?
                    <div className="standing-position">
                      {standing.table.map((e) =>{
                          return(
                          <p className = "team" key={e.position}>
                          {e.position}. {e.team.name}, {e.points} points
                          </p>
                          );
                      })} 
                    </div>
                :null;

            })}
            </div>
        );
    }
        
    
}

export default BplStandings;
