import React, {Component} from 'react';

class BplStandings extends Component{

    state = 
  {
    data: [],
  }

  componentDidMount(){
    fetch('http://localhost:8080/competitions/2021/standings')
    .then(response => response.json())
    .then(data => {
      console.log(data);
      this.setState({data})
    }
    );
    }

    render(){
    return(
            <div className="standing">
              <h1 className="headin">BPL STANDINGS</h1>
              <table>
                <tr className="col">
                    <th>#</th>
                    <th>Team</th>
                    <th>GP</th>
                    <th>GD</th>
                    <th>PTS</th>
                </tr>
                {this.state.data.map((standing) =>{
                  return standing.type == "TOTAL"?
                <div className = "team">
                   {standing.table.map((e) =>{
                          return(
                            <tr className="wpos">
                            <td>{e.position}</td>
                            <td>{e.team.name}</td>
                            <td>{e.team.playedGames}</td>
                            <td>{e.team.goalDifference}</td>
                            <td>{e.team.points}</td>
                            </tr>
                            
                          );
                      })} 
                </div>
                    :null;
        
                
                

            })}
             </table>
            </div>
        );
    }
        
    
}

export default BplStandings;
