import React, {createContext, useContext ,useEffect, useState}from "react";
import { useHistory } from "react-router-dom";
import axios from 'axios';
import { mixed } from "yup";
import "./css/AdminPanel.css"

const AdminPanel = () => {
    const history = useHistory();
    const [users, setUsers] = useState([]);
    const [userId, setUserId] = useState(null);

    console.log(users);
    useEffect(() => {
        fetch(`http://localhost:8080/api/users/`)
        .then((res) => res.json())
        .then((users)=>setUsers(users))
        
    },[]);

    const deleteUser = (id) => {
        console.log("delete user");
        axios.delete(`http://localhost:8080/api/users/${id}`)

    }

    function handleChange(e){
        setUserId(e.target.value);
    }



    return(
        <div className="selects">
        
                <select className="minimal" id="1" value={userId} onChange={handleChange}>
                    {users.map((user) => {
                        return(
                            <option key={user.id} value={user.id}>{user.id} {user.username} {user.roles.name}</option> 
                        );
                    })}
                
                </select>
                <div className="submitDiv">
                <p>Potwierdź zmianę</p>
                    <input className="submitInput" type="submit" value="Click me" />
                </div>
        </div>

    );
};
export default AdminPanel;
