import React, {createContext, useContext ,useEffect, useState}from "react";
import { useHistory } from "react-router-dom";
import axios from 'axios';
import { mixed } from "yup";
import "./css/AdminPanel.css"

const AdminPanel = () => {
    const history = useHistory();
    const [users, setUsers] = useState([]);
    const [userId, setUserId] = useState(null);

    console.log(users);
    useEffect(() => {
        fetch(`http://localhost:8080/api/users/`)
        .then((res) => res.json())
        .then((users)=>setUsers(users))
        
    },[]);

    const deleteUser = (id) => {
        console.log("delete user");
        axios.delete(`http://localhost:8080/api/users/${id}`)

    }

    function handleChange(e){
        setUserId(e.target.value);
    }

    function handleSubmit(e){
        
    }



    return(
        <div className="selects">
            <form className="adminForm" onSubmit={handleSubmit}>
                <select className="minimal" id="1" value={userId} onChange={handleChange}>
                    {users.map((user) => {
                        return(
                            <option key={user.id} value={user.id}>{user.id} {user.username} tak {user.roles.name} siema</option> 
                        );
                    })}
                
                </select>
                <div className="submitDiv">
                <p>Delete user by clicking a button below</p>
                <input className="submitInput" type="submit" value="Delete" />
                </div>
                </form>
        </div>

    );
};
export default AdminPanel;
