import React, {useState, useRef} from "react";
import { useHistory } from "react-router-dom";
import Authentication from "../scripts/authentication";
import "./css/Signup.css"

const Signup = () => {
    const togglePanel = () => {
        history.push("/");
      };

    const form = useRef();
    const checkBtn = useRef();
    const history = useHistory();
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [successful, setSuccessful] = useState(false);
    const [message, setMessage] = useState("");
    const [registerToggle, setRegisterToggle] = useState(false);
    const [loginToggle, setLoginToggle] = useState(true);


      const onClickButton = () => {
        setLoginToggle(!loginToggle);
        setRegisterToggle(!registerToggle);
        
      }
    
      const cardStyleRegister = {
        opacity: registerToggle ? 0 : 1,
        transition: "all 1s ease-in"
      }

      const cardStyleLogin = {
        opacity: loginToggle ? 0 : 1,
        transition: "all 1s ease-in"

      }
      const onChangeUsername = (e) => {
        const username = e.target.value;
        setUsername(username);
      };
    
      const onChangeEmail = (e) => {
        const email = e.target.value;
        setEmail(email);
      };
    
      const onChangePassword = (e) => {
        const password = e.target.value;
        setPassword(password);
      };
    
      const handleRegister = (e) => {
        e.preventDefault();
    
        setMessage("");
        setSuccessful(false);

        
      Authentication.register(username, email, password).then(
        (response) => {
          setMessage(response.data.message);
          setSuccessful(true);
          history.push("/");
          window.location.reload();
        },
        (error) => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          setMessage(resMessage);
          setSuccessful(false);
        }
      );
    };

      return(
            <section className="signup-section">
                <div class="login-page">
  <div class="form-login">
    <form class="register-form" style={cardStyleRegister}>
      <input type="text" placeholder="name"/>
      <input type="password" placeholder="password"/>
      <input type="text" placeholder="email address"/>
      <button>create</button>
      <p class="message" >Already registered? <a onClick={onClickButton}>Sign In</a></p>
    </form> 
    <form class="login-form" style = {cardStyleLogin}>
      <input type="text" placeholder="username"/>
      <input type="password" placeholder="password"/>
      <button>login</button>
      <p class="message" >Not registered? <a onClick={onClickButton}>Create an account</a></p>
    </form>
  </div>
</div>
               
            </section>
      );
    
};
export default Signup;
